import { bootstrapApplication } from '@angular/platform-browser';
import { provideRouter } from '@angular/router';
import { provideHttpClient } from '@angular/common/http';
import { AppComponent } from './app/app.component';
import { HomePage } from './app/pages/home.page';
import { AdminPage } from './app/pages/admin.page'; // <-- si no lo tenías importado aún
import { LoginPage } from './app/pages/login.page'; // <-- NUEVO
const routes = [{
  path: '',
  component: HomePage
}, {
  path: '__',
  redirectTo: '__login',
  pathMatch: 'full'
},
// <-- ruta oculta que lleva al login
{
  path: '__login',
  component: LoginPage
},
// <-- login de admin
{
  path: '__admin',
  component: AdminPage
},
// <-- panel admin
{
  path: '**',
  redirectTo: ''
}];
bootstrapApplication(AppComponent, {
  providers: [provideRouter(routes), provideHttpClient()]
});
